sources.style.css:
*,
*::before,
*::after{
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

html{
    scroll-behavior: smooth;
}

body{
    /* background-color: #1d2021; */
    background-color: #312E37;
    color: #FAFFFD;
    font-size: 1.5rem;
    font-family: 'Noto Sans JP', sans-serif;
    overflow-x: hidden;
}

article{

}

article .article-full-page{
    height: 100vh;
    margin-top: 20vh;
}

.article-full-page h1{
    letter-spacing: 0.2rem;
}

.project-description a {
    margin-right: 0;
}

/* section{
    margin: 25vh 0;
} */

.project-details-section{
    margin: 25vh 0;
}

.home-section{
    /* padding: 5rem 4.25rem; */
    margin: 25vh 0;    
    /* box-shadow: inset 5px 5px 10px #242228, inset -5px -5px 10px #3E3A46; */
}


li {
    list-style: none;
}

.primary-btn{
    font-weight: 600;
    font-size: 0.9em;

    white-space: nowrap;

    padding: 1rem;
    width: fit-content;
    margin: auto;

    transition: 0.3s;
    border-radius: 20px;
    color: #858585;
    /* color: #FAFBFF; */
    background-color: #312E37;
    box-shadow:  8px 8px 16px #242228, -8px -8px 16px #3E3A46;
    text-align: center;
    text-decoration: none;
    cursor: pointer;
}

.primary-btn:hover{
    /* color: #FAFBFF; */
    /* background-color: #312E37; */
    /* box-shadow: -8px -8px 16px #3E3A46, 8px 8px 16px #242228; */
    /* cursor: pointer; */
    /* color: #FFD400; */
    color:#FAFFFD;
}
.primary-btn:active{
    /* color: #FAFBFF; */
    /* background-color: #312E37; */
    /* box-shadow: -8px -8px 16px #3E3A46, 8px 8px 16px #242228; */
    box-shadow: inset 5px 5px 10px #242228, inset -5px -5px 10px #3E3A46;
    /* cursor: pointer; */
    /* color: #FFD400; */
    color:#FAFFFD;
}

.page{
    /* min-height: 65vh;
    display: grid;
    grid-template-columns: 1fr 1fr 1fr;
    flex: 1 0 auto; */
}

.portfolio-intro{
    margin-top: 20vh;
    height: 80vh;
}

.personal {
    margin: auto;
    display: flex;
    height: 100%;
    flex-direction: column;
}

.personal h1{
    margin: auto;
    letter-spacing: .2em;
}

.color-block{
    height: 3rem;
    width: 3rem;
    border-radius: 100%;
    background-color: tomato;
}

.project-tool-wrapper{   
    margin-top: 5rem;
    justify-content: space-between;
    grid-column-start: 1;
    grid-column-end: 4;
}

.project-container a{
    grid-column-end: -1;
    grid-row-end: -1;
    margin-bottom: 0;
}

.project-tools{    
    display: flex;
    margin: 1rem 0;
}

.project-tools li{
    margin: 0 1rem;
}

.project-tools li:first-child{
    margin-left: 0;
}

.highlight-text{
    color: #FFD400;
}



.project-description h3{
    margin-top: 3rem;
}


footer{
    height: 20vh;
    /* background-color: #efefef;
    color: #312e37; */
    background-color: #1f1d22;
    color: #f0f0f1;
    display: flex;
    flex-direction: column;
}

.footer-content ul li {
    list-style: none;
}

.footer-menu{
    display: flex;
    width: 100%;
    justify-content: space-around;
}

.copyright{
    text-align: center;
    margin-bottom: 1em;
    font-size: 1rem;
    color: #898989;
}

nav{
    position: fixed;
    max-width: 1240px;
    box-shadow: 0 4px 2px -2px rgba(0,0,0,.2);
    height: auto;
}

nav ul {
    list-style-type: none;
    margin: 0;
    padding: 0;
    display: flex;
    justify-content: space-evenly;
    flex-wrap: wrap;
}

nav ul li {
    /* margin: 1rem; */
    /* margin: 2.5% auto; */
}

.scrolled{
    /* display: block; */
    /* position: fixed;
    right: 2rem;
    top: 2rem;
    position: -webkit-sticky  */
    /* This is needed for Safari support */
    /* display: ; */
}


#hamburger-links{
    /* display: none; */
}

#hamburger-links a{
    /* display: block; */
}


nav ul li a {
    text-decoration: none;
    text-transform: uppercase;
    font-size: 1rem;
    color: #858585;
}

a {
    text-decoration: none;
    color: inherit;
    color: #858585;
}

li a:hover{
    color: #f0f0f1;
}

nav ul li a:hover{
    color: #f0f0f1;
    cursor: pointer;
}

#nav-on{
    color: #f0f0f1;
}

h1{
    font-size: 5rem;
    font-family: 'Playfair Display', serif;
    /* color: #312e37; */
    color: #FAFFFD;
}

h2{
    font-family: 'Playfair Display', serif;
    font-size: 2rem;
    text-transform: uppercase;
    /* color: #373737; */
    color: #FAFFFD;
}

.hero img{
    object-fit: cover;
    width: 100%;
    max-height: 100%;
    transition-duration: 0.4s;
}

.project-container .hero img:hover{
    padding: 1.75rem;
    box-shadow: inset 5px 5px 10px #242228, inset -5px -5px 10px #3e3a46;
}

.neu-border img{
    padding: 1.75rem;
    box-shadow: inset 5px 5px 10px #242228, inset -5px -5px 10px #3e3a46;
}

.project-cheer span{
    display: inline-block;
    padding-left: 100%;
    animation: marquee 90s linear infinite;
    font-size: 160px;
    will-change: transform; 
    /* animation-direction: normal; */
}

.project-cheer{
    box-shadow: inset 5px 5px 10px #242228, inset -5px -5px 10px #3E3A46;
    grid-column-start: 1;
    grid-column-end: -1;    
    white-space: nowrap;
    overflow: hidden;
    width: 100vw;
    border-bottom: 1px #5e5e5e solid;
    border-top: 1px #5e5e5e solid;
    margin: 20vh 0;
    -webkit-text-stroke: 1px #bebebe;
    -webkit-text-fill-color: #312E37;
}

@-webkit-keyframes marquee{
    0%{
        transform: translate(0, 0);
    }
    100%{
        transform: translate(-100%, 0);
    }
}

.footer-wrapper{
    margin: auto;
}

.footer-wrapper li{
    margin: 1rem;
}

header{
    z-index: 99;
    position: relative;
}

#myLinks{
    display: none;
    max-height: 60vh;
    transition: max-height 0.3s ease;
}

#myLinks.hidden{
    max-height: 0px;
}

.section-title-annotation{
    height: 50vh;
    display:flex;
}

.section-title-annotation h2, .section-title-annotation .annotation-description {
    margin: auto
}

/* project details page */
.project-intro-container{
    margin-top: 25vh;
}

.project-header{
    font-size: 2rem;
}

.project-details-large-header{
    text-align: center;
    font-size: 5rem;
}

.project-details-description{
    line-height: 2.5rem;
}

.project-details-container{
    margin-top: 16.5vh;
}

.team-details{
    margin-top: 2rem;
}

.project-details-container .project-tools-wrapper{
    margin-top: 1rem;
}

.center-container{
    text-align: center;
}

.project-details-hero img{
    object-fit: cover;
    width: 100%;
    max-height: 100%;
    transition-duration: 0.4s;
}

/* end of project details page */


/* media queries */
@media only screen and (min-width: 72em) {
    nav{    
        margin: 2.5% auto;
        width: 1240px;
        max-width: 1240px;
    }

    header{
        z-index: 99;
        position: relative;
        justify-content: center;
        height: 5rem;
        display: flex;
    }
    .project-description p{
        /* font-size: clamp(1.5rem, 2vw, 4rem); */
        font-size: 1.5rem;
        line-height: 1.5em;
        margin-bottom: 2rem;
    }
/*         
    .title-wrapper{
        display:flex;
        justify-content: space-between;
        width: 70%;
        max-width: 32em;
        font-size: 5rem;
        margin-top: 0;
    } */

    .mobile-project-header{
        display: none;
    }

    .desktop-project-header{
        display:flex;
        justify-content: space-between;
        width: 70%;
        max-width: 32em;
        font-size: 5rem;
        margin-top: 0;
        display: block;
    }
    .desktop-nav-container {
        background-color: rgb(49,46,55, .85);
    }
    header{
        z-index: 99;
        position: relative;
        justify-content: center;
        height: fit-content;
        display: flex;
    }
    #hamburger-cta{
        position: fixed;
        top: 2rem;
        right: 2rem;
        position: -webkit-sticky;
        display: none;
    }
    .mobile-nav-container{
        display: none;
    }
    .desktop-nav-container{
        display: block;
    }
}

@media only screen and (max-width: 72em) {
    .project-details-section{
        margin: 8vh 0;
    }
    .mobile-nav-container {
        background-color: rgb(49,46,55, 1);
        display: block;
    }
    .desktop-nav-container{
        display: none;
    }
    #hamburger-cta{
        position: fixed;
        top: 2rem;
        right: 2rem;
        position: -webkit-sticky;
        display: block;
    }
    body{
        font-size: 1rem;
    }

    footer{
        flex-shrink: 0;
        margin-top: auto;
        height: 20vh;
        display: flex;
        flex-direction: column;
    }

    .mobile-project-header{
        display: block;
        display:flex;
        justify-content: space-between;
        width: 70%;
        max-width: 32em;
        font-size: 3rem;
        margin-top: 0;
        display: block;
    }

    .desktop-project-header{
        display: none;
    }

    /* .title-wrapper{
        display:flex;
        justify-content: space-between;
        width: 70%;
        max-width: 32em;
        font-size: 3rem;
        margin-top: 0;
    } */

    .project-description p{
        font-size: clamp(1.5rem, 2vw, 4rem);
        margin-bottom: 2rem;
    }

    .footer-content ul li {
        list-style: none;
    }

    .footer-menu{
        display: flex;
        width: 100%;
        justify-content: space-around;
    }

    .hero{
        grid-column: 2/3;
        margin: auto;
    }

    nav ul li {
        margin: 3rem;
        width: 100vw;
        text-align: center;    
    }

    .project-tool-wrapper {    
        margin-top: 2rem;
    }
}


@media only screen and (max-width: 32em) {
    h1{
        font-size: 3rem;
    }
}

/* s8 */
@media only screen and (max-width: 25em) {
    h1{
        font-size: 1.25rem;
    }
    .color-block{
        width: 2rem;
        height: 2rem;
        background-color: tomato;
    }
    .project-description p {
        font-size: 1.25rem;
        margin-bottom: 2rem;
    }
    section {
        padding: 3rem 1.75rem;
    }
    .article-full-page h1{
        line-height: 2em;
        font-size: 1.5rem;
    }
    .portfolio-intro h1{
        font-size: 2rem;
    }
    .portfolio-intro h2{
        font-size: 1.2rem;
    }
    .portfolio-intro p{
        font-size: 1.25rem;
        letter-spacing: .2em;
        margin-top: .5rem;
    }
}
